import axios from 'axios'
const baseUrl = '/api/blogs'

let token = `bearer ${token}`

setToken = newToken => {
  token = newToken
}

const getAll = () => {
  const request = axios.get(baseUrl)
  return request.then(response => response.data)
}

const post = (blog) => {
  const config = {
    Authorization: token
  }
}

export default { getAll }
