import React, { useState } from 'react'
const Blog = ({blog}) => {
  const [fullInfo, setFullInfo] = useState(false)

  return(
    <>
      {    
        !fullInfo &&
          <div>
            <span>
              {blog.title} {blog.author}
            </span>  
            <button onClick={() => setFullInfo(true)}>info</button>
          </div>
      }
      {
        fullInfo &&
          <div>
            <span>
              {/* {JSON.stringify(blog)} */}
              {blog.title} {blog.author}
              {blog.url}
              likes {blog.likes}
              {blog.user.username}
            </span>  
            <button onClick={() => setFullInfo(false)}>hide info</button>
          </div>
      }
    </>
  )
}

export default Blog
