import React, { useState } from 'react'
// import blogService from '../services/blogs'

const Blog = ({ blog, liked }) => {
  const [fullInfo, setFullInfo] = useState(false)

  return(
    <>
      {
        !fullInfo &&
          <div>
            <span>
              {blog.title} {blog.author}
            </span>
            <button style={{ marginLeft: '10px' }} onClick={() => setFullInfo(true)}>info</button>
          </div>
      }
      {
        fullInfo &&
          <div>
            <span>
              {/* {JSON.stringify(blog)} */}
              <div>{blog.title} {blog.author}</div>
              <div>{blog.url}</div>
              <div>likes {blog.likes}</div>
              <div>{ blog.user ? blog.user.username : '' }</div>
            </span>
            <button onClick={() => setFullInfo(false)}>hide info</button>
          </div>
      }
      <button onClick={() => liked()}>like</button>
    </>
  )
}

export default Blog
