import axios from 'axios'
const baseUrl = '/api/blogs'

let token = null

setToken = newToken => {
  token = newToken
}

const getAll = () => {
  const request = axios.get(baseUrl)
  return request.then(response => response.data)
}

const post = (blog) => {
  const config = {
    Authorization: `bearer ${token}`
  }
}

export default { getAll }
