import React from 'react'
import { useSelector } from 'react-redux'

const Notification = () => {
  const notification = useSelector(state => state.notification)

  const style = {
    border: 'solid',
    padding: 10,
    borderWidth: 1
  }

  return (
    <>
      {
        notification && 
          <div style={style}>
          </div>
      }
      <div>{notification}</div>
    </>
  )
}

export default Notification
