const reducer = (state = '', action) => {
  switch(action.type) {
    case 'SHOW':
      return action.data
    default:
    return state
  }
}

let hideTimeout

export const showNotification = (message, time) => {
  clearTimeout(hideTimeout)
  hideTimeout = setTimeout(() => hideNotification(), time * 1000)

  return {
    type: 'SHOW',
    data: message
  }
}

export const hideNotification = () => {
  return {
    type: 'HIDE'
  }
}

export default reducer
