import { useDispatch } from 'react-redux'

const reducer = (state = '', action) => {
  switch(action.type) {
    case 'SHOW':
      return action.data
    case 'HIDE':
      return ''
    default:

    return state
  }
}

let hideTimeout

export const showNotification = (message, time = 5) => {
  clearTimeout(hideTimeout)

  return {
    type: 'SHOW',
    data: message
  }
}

export const hideNotification = () => {
  return {
    type: 'HIDE'
  }
}

export default reducer
