import React, {useState} from 'react'
const Blog = ({}) => {
  const [blogTitle, setBlogTitle] = useState('')
  const [blogAuthor, setBlogAuthor] = useState('')
  const [blogUrl, setBlogUrl] = useState('')

  const addBlog = (e) => {
    e.preventDefault()
    blogService.post({author: blogAuthor, title: blogTitle, url: blogUrl })
      .then(e => {
        setBlogs(blogs.concat(e))
        showNotification({type: 'success', text: 'blogin lis채채minen onnistui!'})
      })
      .catch(e => {
        showNotification({type: 'success', text: e.response.data.error})
      })

      toggleableRef.current.toggleVisibility()
  }

  return(
    <>
      <h3>+ New blog</h3>
      <form onSubmit={() => addBlog('jea')}>
        <input value={blogTitle} placeholder="title" onChange={({target}) => setBlogTitle(target.value)}/><br/>
        <input value={blogAuthor} placeholder="author" onChange={({target}) => setBlogAuthor(target.value)}/><br/>
        <input value={blogUrl} placeholder="url" onChange={({target}) => setBlogUrl(target.value)}/>
        <button type="submit">send</button>
      </form>
    </>
  )
}

export default Blog
