import blogService from '../services/blogs'
import showNotification from './notificationReducer'

const reducer = (state = [], action) => {
  switch(action.type) {
    case 'ADD':
      return [...state, action.data]
    case 'INIT':
      return action.data
    default:

    return state
  }
}

export const addBlog = async ({ blogAuthor, blogTitle, blogUrl }) => {
  return async dispatch => {
    blogService.post({ author: blogAuthor, title: blogTitle, url: blogUrl })
      .then(e => {
        dispatch({ type: 'ADD', data: e.data })
        // dispatch(showNotification({ type: 'success', text: 'blogin lis채채minen onnistui!' }))
      })
      // .catch(e => {
      //   dispatch(showNotification({ type: 'success', text: e.response ? e.response.data.error : 'Jotain meni vikaan :(' }))
      // })
  }
}

export const initBlogs = () => {
  return async dispatch => {
    blogService.getAll().then(blogs => {
      dispatch({
        type: 'INIT',
        data: blogs
      })
    })
  }
}

export default reducer
