import React, { useState, useEffect } from 'react'
import Blog from './components/Blog'
import Notification from './components/Notification'
import blogService from './services/blogs'
import loginService from './services/login'

const App = () => {
  const [blogs, setBlogs] = useState([])
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [blogTitle, setBlogTitle] = useState('')
  const [blogAuthor, setBlogAuthor] = useState('')
  const [blogUrl, setBlogUrl] = useState('')
  const [user, setUser] = useState(null)
  const [notification, setNotification] = useState({text: '', type: 'success', visible: false})

  useEffect(() => {
    blogService.getAll().then(blogs =>
      setBlogs( blogs )
    )  
  }, [])

  const showNotification = ({type, text, duration}) => {
    setNotification({type, text})
    setTimeout(() => {
      setNotification({type: 'success', text: '', visible: false})
    }, duration)
  }

  const logout = () => {
    window.localStorage.removeItem('blogUser')
    blogService.setToken(null)
    setUser(null)
  }

  const login = () => {
    loginService.login({username, password})
      .then(e => {
        setUser(e.data)
        setUsername('')
        setPassword('')
        window.localStorage.setItem('blogUser', JSON.stringify(e.data))
      })
      .catch(e => {
        alert(JSON.stringify(e))
      })
  }

  const addBlog = (e) => {
    e.preventDefault()
    blogService.post({author: blogAuthor, title: blogTitle, url: blogUrl })
      .then(e => {
        setBlogs(blogs.concat(e))
        setBlogAuthor('')
        setBlogUrl('')
        setBlogTitle('')
      })
      .catch(e => {
        alert(e)
      })
  }

  useEffect(() => {
    const loggedUserJSON = window.localStorage.getItem('blogUser')
    if (loggedUserJSON) {
      const user = JSON.parse(loggedUserJSON)
      setUser(user)
      blogService.setToken(user.token)
    }
  }, [])

  return (
    <div>
      <h2>blogs</h2>
      {
        !user &&
          <div>
            <h4>login</h4>

            <input value={username} onChange={({target}) => setUsername(target.value)} /><br></br>
            <input type="password" value={password} onChange={({target}) => setPassword(target.value)} />
            <button onClick={login}>login</button>
          </div> 
      }
      {
        notification.visible &&
        <Notification text={notification.text} type={notification.type}></Notification>
      }
      {
        user &&
            <>
              {user.username} <button onClick={logout}>logout</button>
              <div style={{'marginBottom': '30px'}}>
                <h3>+ New blog</h3>
                <form onSubmit={addBlog}>
                  <input placeholder="title" onChange={({target}) => setBlogTitle(target.value)}/><br/>
                  <input placeholder="author" onChange={({target}) => setBlogAuthor(target.value)}/><br/>
                  <input placeholder="url" onChange={({target}) => setBlogUrl(target.value)}/>
                  <button type="submit">send</button>
                </form>
              </div>
              {
                blogs.map(blog =>
                  <Blog key={blog.id} blog={blog} />
                )
              }
            </>
      }
    </div>
  )
}

export default App
