import React, { useState } from 'react'
const Blog = ({blog}) => {
  const [fullInfo, setFullInfo] = useState(false)

  return(
    <>
      {    
        !fullInfo &&
          <>
            <div>
              {blog.title} {blog.author}
            </div>  
            <button onClick={() => setFullInfo(true)}></button>
          </>
      }
      {
        fullInfo &&
          <>
            <div>
              {JSON.stringify(blog)}
            </div>  
            <button onClick={() => setFullInfo(false)}></button>
          </>
      }
    </>
  )
}

export default Blog
