import React, { useState } from 'react'

const Books = (props) => {
  const [genre, setGenre] = useState('')

  const genres = props.books.map(book => book.genres).flatten()

  if (!props.show) {
    return null
  }

  return (
    <div>
      <h2>books</h2>

      <table>
        <tbody>
          <tr>
            <th></th>
            <th>
              author
            </th>
            <th>
              published
            </th>
          </tr>
          {props.books.map(a =>
            <tr key={a.title}>
              <td>{a.title}</td>
              <td>{a.author ? a.author.name : ''}</td>
              <td>{a.published}</td>
            </tr>
          )}
          { genres }
        </tbody>
      </table>
    </div>
  )
}

export default Books
