import { gql } from '@apollo/client'

export const ALL_BOOKS = gql`
query {
  allBooks {
    author
    title
    published
    id
    genres
  }
} 
`

export const ADD_BOOK = gql`
mutation($title: String!, $published: Int!, $author: String!, $genres: [String!]!) {
  addBook(
    title: $title, 
    published: $published, 
    author: $author, 
    genres: $genres
  ) {
    name
  }
}
`

export const ALL_AUTHORS = gql`
query {
  allAuthors {
    name
    born
    bookCount
  }
}
`
