import React, { useState } from 'react'
import Select from 'react-select'

const Authors = (props) => {
  const [author, setAuthor] = useState('')
  if (!props.show) {
    return null
  }
  return (
    <div>
      <h2>authors</h2>
      <table>
        <tbody>
          <tr>
            <th></th>
            <th>
              born
            </th>
            <th>
              books
            </th>
          </tr>
          {props.authors.map(a =>
            <tr key={a.name}>
              <td>{a.name}</td>
              <td>{a.born}</td>
              <td>{a.bookCount}</td>
            </tr>
          )}
        </tbody>
      </table>
      <div>
        <h3>Set birthyear</h3>
        <Select 
          options={
            props.authors.map(
              a => { 
                return { value: a.name, label: a.name } 
              }
            )
          }
          onChange={ (e) => setAuthor(e) }

        />
      </div>
    </div>
  )
}

export default Authors
